mapper.select:
SELECT a.ID, a.GROUPCODE, a.ARCHIVECATEGORY, a.TITLE, a.PDFFILE AS "file" FROM CSPDF a
JOIN CODEDETAIL b ON a.GROUPCODE = b.GROUPCODE AND a.ARCHIVECATEGORY = b.code
WHERE a.ID = #{id}

SELECT a.ID, a.GROUPCODE, a.ARCHIVECATEGORY, a.TITLE,a.CREATEDAT, a.PDFFILE AS "file", b.CODENAME FROM CSPDF a JOIN CODEDETAIL b ON a.GROUPCODE = b.GROUPCODE AND a.ARCHIVECATEGORY = b.code
AND a.GROUPCODE = #{groupCode}
AND a.ARCHIVECATEGORY = #{archiveCategory}
ORDER BY a.ID DESC OFFSET #{pageRequestDTO.offset} ROWS FETCH NEXT #{pageRequestDTO.size} ROWS ONLY

SELECT COUNT(*) FROM CSPDF


        AND GROUPCODE = #{groupCode}


        AND ARCHIVECATEGORY = #{archiveCategory}

mapper.update:
