mapper.select:
SELECT a.ID, a.GROUPCODE, a.DISCLOSURECATEGORY, a.TITLE,a.CREATEDAT, a.PDFFILE AS "file", a.TERM1, a.TERM2, a.TERM3, b.CODENAME FROM DISCLOSURE a
JOIN CODEDETAIL b ON a.GROUPCODE = b.GROUPCODE AND a.DISCLOSURECATEGORY = b.code
WHERE a.ID = #{id}

SELECT a.ID, a.GROUPCODE, a.DISCLOSURECATEGORY, a.TITLE,a.CREATEDAT, a.PDFFILE AS "file", a.TERM1, a.TERM2, a.TERM3, b.CODENAME FROM DISCLOSURE a JOIN CODEDETAIL b ON a.GROUPCODE = b.GROUPCODE AND a.DISCLOSURECATEGORY = b.code
AND a.GROUPCODE = #{groupCode}
AND a.DISCLOSURECATEGORY = #{disclosureCategory}
ORDER BY a.ID DESC OFFSET #{pageRequestDTO.offset} ROWS FETCH NEXT #{pageRequestDTO.size} ROWS ONLY

SELECT COUNT(*) FROM DISCLOSURE


        AND GROUPCODE = #{groupCode}


        AND DISCLOSURECATEGORY = #{disclosureCategory}

mapper.update:
UPDATE DISCLOSURE SET
GROUPCODE = #{groupCode}, DISCLOSURECATEGORY = #{disclosureCategory},
TITLE = #{title},  PDFFILE = #{file},
TERM1 = #{term1, jdbcType=VARCHAR}, TERM2 = #{term2, jdbcType=VARCHAR},
TERM3 = #{term3, jdbcType=VARCHAR}
WHERE ID = #{id}

