mapper.select:
SELECT ID, INVESTTYPE, TITLE, INVESTFILE AS "file", CREATEDAT FROM INVEST
INVESTTYPE = #{investType}
ORDER BY ID DESC OFFSET #{pageRequestDTO.offset} ROWS FETCH NEXT #{pageRequestDTO.size} ROWS ONLY

mapper.update:
UPDATE INVEST SET
INVESTTYPE = #{investType}, TITLE = #{title},
INVESTFILE = #{file,jdbcType=VARCHAR}
WHERE ID = #{id}

mapper.delete:
DELETE FROM INVEST
WHERE ID IN

    #{id}
